def candidate(a, b):
    # Initialize a set to store unique common divisors
    common_divisors = set()

    # Iterate over all numbers from 1 to 'a'
    for i in range(1, a + 1):
        # Check if 'i' is a divisor of both 'a' and 'b'
        if a % i == 0 and b % i == 0:
            # Add 'i' to the set of common divisors
            common_divisors.add(i)

    # Return the sum of common divisors
    return sum(common_divisors)
