def candidate(arr, p):
    # Define the operation that needs to be performed on each element in arr
    def even_odd(num):
        if num % 2 == 0:
            return 0
        else:
            return 1

    # Create a new array with transformed values using the even_odd function
    for _ in range(p):
        new_arr = [even_odd(num) for num in arr]

    # Check if the last element is even or odd and return the result
    return "EVEN" if new_arr[-1] == 0 else "ODD"
