def candidate(s):
    # Initialize an empty list to store the substrings
    result = []

    # Initialize an empty substring
    substring = ""

    # Iterate over each character in the input string
    for char in s:
        # If the character is a lowercase letter, add the current substring to the result and reset it
        if char.islower():
            if substring:
                result.append(substring)
            substring = ""

        # Otherwise, append the character to the current substring
        else:
            substring += char

    # Add the last substring to the result
    if substring:
        result.append(substring)

    return result
