def candidate(lst):
    # Check if the list is empty
    if not lst:
        return True

    # Initialize a variable to store the first element of the list
    first_element = lst[0]

    # Iterate over each element in the list, starting from the second one
    for element in lst[1:]:
        # If an element does not match the first one, return False
        if element != first_element:
            return False

    # If we have checked all elements and haven't returned False, return True
    return True
