def candidate(s):
    # Initialize an empty list to store the characters at even indices
    result = []

    # Iterate over the input string with enumerate to get both index and character
    for i, char in enumerate(s):
        # Check if the index is even (i.e., not odd)
        if i % 2 == 0:
            # Append the character to the result list
            result.append(char)

    # Return the resulting list of characters
    return ''.join(result)
