def candidate(list1, list2):
    # Convert input lists to sets to remove duplicates and improve lookup efficiency
    set1 = set(list1)
    set2 = set(list2)

    # Use a set comprehension to find the intersection of the two sets without duplicates
    # Filter out elements that are not common in both sets
    intersection = {element for element in set1 if element in set2}

    # Return the result as a list
    return list(intersection)
