def candidate(arr, k):
    """
    Find the first element occurring k times in a given array.

    Args:
        arr (list): The input array.
        k (int): The number of occurrences for the target element.

    Returns:
        tuple: A tuple containing the first occurrence index and the value itself.
    """

    # Create a dictionary to store elements and their counts
    counts = {}

    # Iterate over the array and count occurrences of each element
    for i, num in enumerate(arr):
        # Increment the count for each occurrence of an element
        if num in counts:
            counts[num] += 1
        else:
            counts[num] = 1

    # Find the first element occurring k times
    for index, (num, count) in enumerate(counts.items()):
        if count == k:
            return index, num
