def candidate(lst):
    # Initialize an empty dictionary to store counts
    counts = {}

    # Iterate over the list to count occurrences
    for item in lst:
        if item in counts:
            counts[item] += 1
        else:
            counts[item] = 1

    # Find the key with maximum count
    max_count = max(counts.values())
    return [item for item, count in counts.items() if count == max_count][0]
