def candidate(input_list):
    # Check if the input list is not empty
    if len(input_list) > 0:
        # Create a new list with the first element of the original list
        new_list = [input_list[-1]]

        # Add all elements except the last one to the new list
        for i in range(len(input_list) - 1):
            new_list.append(input_list[i])

    return new_list
