def candidate(*lists):
    # Initialize an empty dictionary to store the result
    result = {}

    # Iterate over each list in pairs
    for i, (key, value) in enumerate(zip(lists[::2], lists[1::2])):
        # If the current key is not a string, convert it to a string
        if not isinstance(key, str):
            key = ''.join(map(str, key))

        # If the current key does not exist in the result, create it as an empty dictionary
        if key not in result:
            result[key] = {}

        # Iterate over each element in the current list
        for j, elem in enumerate(value):
            # Assign the element to its corresponding index in the outer dictionary
            result[key][j] = elem

    return result
