def candidate(a, b, c):
    # Sort the input numbers in ascending order
    nums = sorted([a, b, c])

    # Check if the numbers have an odd or even count
    if len(nums) % 2 == 0:
        # If they are equal, return the mean of the three
        return (nums[0] + nums[1] + nums[2]) / 3
    else:
        # Return the middle value
        return nums[len(nums) // 2]
