# Python
def candidate(n):
    # Initialize a variable to store the sum of even factors.
    even_sum = 0

    # Iterate over all numbers from 1 to n (inclusive).
    for i in range(1, n + 1):
        # Check if i is an even factor by using the modulus operator (%).
        if n % i == 0:
            # If i is an even factor, add it to the total sum.
            if i % 2 == 0:
                even_sum += i

    # Return the calculated sum of even factors.
    return even_sum
