def candidate(lst, position=None, indices=None):
    # Check if lst is a two-dimensional list
    if not isinstance(lst[0], list):
        raise ValueError("Invalid input type")

    result = []

    if position is not None:
        # If position is provided, extract each element at that index from all rows
        for row in lst:
            result.append(row[position])
    elif indices is not None:
        # If indices are provided, extract the elements at those positions
        for index in indices:
            result.append(lst[index])

    return result
