import math

def candidate(a, r, n):
    # Check if the series is geometric progression
    if r != 1:
        # Calculate the sum using the formula for the sum of a finite geometric series when r != 1
        S = a * (1 - r ** n) / (1 - r)
        return int(S)

    # If r is 1, the sum is simply a times n plus a divided by 2
    else:
        return a * n + a // 2
