def candidate(list1, list2):
    # Convert both lists to sets to efficiently check for common elements and their order
    set1 = set(list1)
    set2 = set(list2)

    # Check if there are any common elements between the two sets
    if not set1.intersection(set2):
        return False

    # Create a list of common elements in order, using set intersection to maintain the correct order
    common_elements = [element for element in set1 & set2]

    # Iterate over the original lists to find the corresponding elements in the same order
    i, j = 0, 0
    while i < len(list1) and j < len(list2):
        if list1[i] == common_elements[j]:
            j += 1
        elif list1[i] != common_elements[j]:
            return False
        else:
            i += 1

    # If all elements are found in the same order, return True
    return i == len(list1) and j == len(list2)
