def candidate(lst, n):
    # Check if n is within bounds of the list
    if n < 1:
        raise IndexError("Index out of range")

    # Initialize an empty list to store the selected elements
    result = []

    # Iterate over the list and append elements to the result list until it reaches the desired length
    for i in range(n):
        # Check if there are enough elements left in the list before appending the next one
        if i < len(lst) - 1:
            result.append(lst[i])  # Use i instead of i+1 to get the nth element
        else:
            result.append(lst[i])  # If we're at the last element, just append it

    return result
