def candidate(strings, target):
    # Initialize an empty list to store the results
    result = []

    # Define a subfunction to check if a string consists only of digits
    def is_numeric(s):
        return s.isdigit()

    # Iterate over each element in the input list
    for s in strings:
        # Check if the element is numeric
        if is_numeric(s):
            # If it's numeric, replace it with the target string prepended and appended with spaces
            result.append(target + ' ' + s + ' ' + target)
        else:
            # If it's not numeric, keep it as is
            result.append(s)

    # Return the updated list converted to a single string
    return ''.join(result)
