def candidate(s):
    # Initialize an empty list to store the result
    result = []

    # Iterate over each character in the input string
    for char in s:
        # If the character is a space, replace it with the specified sequence
        if char == ' ':
            result.append('%20')
        else:
            # Otherwise, just append the character to the result list
            result.append(char)

    # Join all characters in the result list into a single string
    return ''.join(result)
