def candidate(nums):
    # Initialize variables to store the first even and odd numbers
    first_even = None
    first_odd = None

    # Iterate through the list and identify even and odd numbers
    for num in nums:
        if num % 2 == 0:
            if first_even is None:
                first_even = num
        else:
            if first_odd is None:
                first_odd = num

    # Return the product of first even and odd number (or None if not found)
    return first_even * first_odd if first_even is not None and first_odd is not None else None
