def candidate(x1, y1, x2, y2):
    # Check for vertical line
    if x1 == x2:
        # Vertical lines are undefined in slope, so return "Vertical line"
        return "Vertical line"

    # Calculate slope using the formula m = (y2 - y1)/(x2 - x1)
    # This will always be a float value when denominator is not zero
    if x1 == x2:
        m = 0
    else:
        m = (y2 - y1) / (x2 - x1)

    return m
