def candidate(n):
    # Calculate factorial recursively
    def factorial(x):
        if x == 0 or x == 1:
            return 1
        else:
            return x * factorial(x-1)

    # Calculate the number of digits in the factorial
    num_digits = 0
    fact_value = factorial(n)

    # Count the number of digits
    while fact_value > 0:
        num_digits += 1
        fact_value //= 10

    return num_digits
