def candidate(lst):
    # Check if lst is a list
    if not isinstance(lst, list):
        raise TypeError("Input must be a list")

    # Initialize variables to store sums of first even and odd numbers
    even_sum = 0
    odd_sum = 0

    # Iterate over each element in the input list
    for num in lst:
        # If number is even, add it to even sum; if number is odd, add it to odd sum
        if num % 2 == 0:
            # Check if we have already found an even number
            if not even_sum:
                even_sum = num
        else:
            # Check if we have already found an odd number
            if not odd_sum:
                odd_sum = num

    # Return the total sum of both sums
    return even_sum + odd_sum
