    combined_dict = {}

    # Iterate over the key-value pairs of the first dictionary
    for key, value in dict1.items():
        # Check if the key is present in both dictionaries
        if key in dict2:
            # If the key is common to both dictionaries, add its values
            combined_dict[key] = value + dict2[key]
        else:
            # If the key only exists in the first dictionary, include it in the output
            combined_dict[key] = value

    # Iterate over the key-value pairs of the second dictionary
    for key, value in dict2.items():
        # Check if the key is not already present in the combined dictionary
