def candidate(lst):
    # Initialize counter to keep track of elements until tuple is found
    count = 0

    # Iterate over each element in the list
    for elem in lst:
        # Check if element is not a tuple and is either int or float
        if isinstance(elem, (int, float)):
            # Increment counter
            count += 1
        # If element is a tuple, break out of loop
        elif isinstance(elem, tuple):
            break

    return count
