def candidate(lst_of_lists):
    # Initialize an empty dictionary to store the frequency counts
    freq_dict = {}

    # Iterate over each inner list in the input list of lists
    for inner_list in lst_of_lists:
        # Iterate over each element in the current inner list
        for elem in inner_list:
            # If the element is already in the dictionary, increment its count
            if elem in freq_dict:
                freq_dict[elem] += 1
            # Otherwise, add it to the dictionary with a count of 1
            else:
                freq_dict[elem] = 1

    return freq_dict
